def approximate_log(x):
    if x <= 0:
        raise ValueError("输入必须是一个正数")
    elif x < 1:
        return log(x) - 2 * log(1.5)
    else:
        return log(x) - 2 * log(1.72)

x = float(input("请输入一个正数 x: "))
approximate_value = approximate_log(x)
print("x 的对数（以 e 为底）的近似值为: {:.4f}".format(approximate_value))
```

