  // 初始化一个变量，用于存储最大元素
  let max = arr[0];

  // 遍历数组中的每个元素
  for (let i = 1; i < arr.length; i++) {
    // 如果当前元素大于已知的最大元素，则更新最大元素
    if (arr[i] > max) {
      max = arr[i];
    }
  }

  // 返回最大元素
